import React from "react";
import api from "../utils/api";
import { useState, useEffect } from "react";
function Main(props) {
  const [userName, setUserName] = useState("");
  const [userDescription, setUserDescription] = useState("");
  const [userAvatar, setUserAvatar] = useState("");
  const [cards, setCards] = useState([]);

  useEffect(() => {
    api
      .getUserData()
      .then((data) => {
        setUserName(data.name);
        setUserDescription(data.about);
        setUserAvatar(data.avatar);
      })
      .catch((error) => {
        console.error(
          `ошибка загрузки данных с апи: ${error} - ${error.statusText}`
        );
      });
  }, [userName, userDescription, userAvatar]);

  return (
    <main className="main-page">
      <section className="profile">
        <button className="profile__avatar-button" onClick={props.onEditAvatar}>
          <img className="profile__avatar" src={userAvatar} alt="Кусто" />
        </button>
        <div className="profile__info">
          <button
            onClick={props.onEditProfile}
            type="button"
            className="profile__edit-button"
          ></button>
          <h1 className="profile__name">{userName}</h1>
          <p className="profile__desc-profile">{userDescription}</p>
        </div>
        <button
          type="button"
          className="profile__add-button"
          onClick={props.onAddPlace}
        ></button>
      </section>
      <section className="cards">
        {cards.map((card) => (
          // <template key={card._id} className="template" id={card.id}>
          <article key={card._id} className="card">
            <button className="card__trashcan" type="button"></button>
            <img className="card__image" src={card.link} alt={card.name} />
            <div className="card__block">
              <h2 className="card__heading">{card.name}</h2>
              <button className="card__like" type="button">
                <p className="card__likes-number">{card.likes.length}</p>
              </button>
            </div>
          </article>
          // </template>
        ))}
      </section>
    </main>
  );
}

export default Main;
